import Dialog from '@mui/material/Dialog';
import Button from '@mui/material/Button';
import { MdClose } from "react-icons/md";
import Rating from '@mui/material/Rating';
import Slider from 'react-slick';
import { useRef } from 'react';
import InnerImageZoom  from 'react-inner-image-zoom';
import 'react-inner-image-zoom/lib/InnerImageZoom/styles.css';
import QuantityBox from '../QuantityBox';
import { IoHeartOutline } from "react-icons/io5";
import { MdOutlineCompareArrows } from "react-icons/md";


const ProductModal =(props)=>{

    const [slideIndex, setSlideIndex] = useState(0);
    const zoomSliderBig = useRef();
    const zoomSlider = useRef();

    const goto=(index) =>{
        setSlideIndex(index);
        zoomSlider.current.swiper.slideTo(index);
        zoomSliderBig.current.swiper.slideTo(index);
    }
    return (
        <>
            <Dialog  open={true} className="productModal" onClose={()=>props.closeProductModal()} >
                <Button className='close_'  onClick={()=>props.closeProductModal()}><MdClose/></Button>
                <h4 class="mb-1 font-weight-bold"> All New Stylish dresses </h4>
                <div className='d-flex align-items-center'>
                    <div className='d-flex align-items-center mr-4'>
                        <span> Brands:</span>
                        <span className='ml-2'><b> Welch's </b> </span>
                    </div>
                    <Rating  name="read-only" value={5} readOnly size="small" precision={0.5}/>
                    <hr/>
                </div>   

                <div className='row mt-2 productDetailsModal'>
                    <div className='col-md-5'>
                        <div className='productZoom position-relative'>
                            <div className='badge badge-primary'>23%</div>
                            <Swiper
                                slidesPerView={1}
                                spaceBetween={0}
                                navigation={false}
                                slidesPerGroup={1}
                                modules={[Navigation]}
                                className="zoomSliderBig"
                                ref={zoomSliderBig}>
                                    <SwiperSlide>
                                        <div className='item'>
                                            <InnerImageZoom
                                                zoomType="hover" zoomScale={1}
                                                src={`https://m.media-amazon.com/images/I/61YamEA82eL._SY741_.jpg`}/>

                                        </div>
                                    </SwiperSlide>

                                    <SwiperSlide>
                                        <div className='item'>
                                            <InnerImageZoom
                                                zoomType="hover" zoomScale={1}
                                                src={`https://m.media-amazon.com/images/I/61-gvqLFqhL._SY741_.jpg`}/>

                                        </div>
                                    </SwiperSlide>

                                    <SwiperSlide>
                                        <div className='item'>
                                            <InnerImageZoom
                                                zoomType="hover" zoomScale={1}
                                                src={`https://m.media-amazon.com/images/I/61ZwIOqhP4L._SY741_.jpg`}/>

                                        </div>
                                    </SwiperSlide>

                                    <SwiperSlide>
                                        <div className='item'>
                                            <InnerImageZoom
                                                zoomType="hover" zoomScale={1}
                                                src={`https://m.media-amazon.com/images/I/71nJAW-QBfL._SY741_.jpg`}/>

                                        </div>  
                                    </SwiperSlide>
                                </Swiper>
                        </div>


                        <Swiper
                            slidesPerView={4}
                            spaceBetween={0}
                            navigation={false}
                            slidesPerGroup={1}
                            modules={[Navigation]}
                            className="zoomSliderBig"
                            ref={zoomSlider}>

                                <SwiperSlide>
                                    <div className={`item ${slideIndex===0 && 'item_active'}`}>
                                    <img src={`https://m.media-amazon.com/images/I/61YamEA82eL._SY741_.jpg`} 
                                        className='w-100' onClick={() => goto(0)}/>
                                    </div>
                                </SwiperSlide>
                                <SwiperSlide>
                                    <div className={`item ${slideIndex===0 && 'item_active'}`}>
                                    <img src={`https://m.media-amazon.com/images/I/61-gvqLFqhL._SY741_.jpg`} 
                                        className='w-100' onClick={() => goto(0)}/>
                                    </div>
                                </SwiperSlide>
                                <SwiperSlide>
                                    <div className={`item ${slideIndex===0 && 'item_active'}`}>
                                    <img src={`https://m.media-amazon.com/images/I/61ZwIOqhP4L._SY741_.jpg`} 
                                        className='w-100' onClick={() => goto(0)}/>
                                    </div>
                                </SwiperSlide>
                                <SwiperSlide>
                                    <div className={`item ${slideIndex===0 && 'item_active'}`}>
                                    <img src={`https://m.media-amazon.com/images/I/71nJAW-QBfL._SY741_.jpg`} 
                                        className='w-100' onClick={() => goto(0)}/>
                                    </div>
                                </SwiperSlide>

                        </Swiper>

                    </div>
                    <div className='col-md-7'>
                        <div className='d-flex info align-items-center mb-3'>
                            <span className='oldPrice lg mr-2'> $9.35</span>
                            <span className='netPrice text-danger lg'> $7.35</span>
                        </div>
                        <span className='badge bg-success'> IN STOCK </span>
                        <p className='mt-3'>fdfjldfdfkldffffjfldjf. Class djfdfjdlkfjdjkjlkj torquent</p>
                        
                        <div className='d-flex align-items-center'>
                            <QuantityBox/>
                            <Button className='btn-blue btn-lg btn-big btn-round ml-3'> Add to Cart</Button>
                        </div>

                        <div className='d-flex align-items-center mt-5 actions'>
                        <Button  className='btn-round btn-sml' variant="outlined"> <IoHeartOutline/> &nbsp; ADD TO WISHLIST</Button>
                        <Button  className='btn-round btn-sml ml-3' variant="outlined"> <MdOutlineCompareArrows/> &nbsp;COMPARE</Button>
                        </div>
                        
                    </div>
                </div>            

            </Dialog>
        </>
        
    )
}
export default ProductModal;
