import React, { useEffect } from 'react';
import Button from '@mui/material/Button';
import { FaAngleDown } from "react-icons/fa6";
import DialogTitle from '@mui/material/DialogTitle';
import Dialog from '@mui/material/Dialog';
import { IoIosSearch } from "react-icons/io";
import { MdClose } from "react-icons/md";
import { useState } from 'react';
import Slide from '@mui/material/Slide';
import { useContext } from 'react';
import { MyContext } from '../../App';



const Transition = React.forwardRef(function Transition(props, ref) {
    return <Slide direction="up" ref={ref} {...props} />;
  });

const CountryDropdown = () => {

    const [isOpenModal, setisOpenModal] = useState(false);
    const [selectedTab, setselectedTab] = useState(null);

    const [countryList, setcountryList] = useState([]);

    const context = useContext(MyContext);

    const selectCountry=(index, country)=>{
        setselectedTab(index);
        setisOpenModal(false);
        context.setselectedCountry(country)
    }
    useEffect(()=>{
        setcountryList(context.countryList);
    },[])

    const filterList=(e)=>{
        const keyword = e.target.value.toLowerCase();
        
        if(keyword!==""){
            const list = countryList.filter((item)=>{
                return item.country.toLowerCase().includes(keyword);
            });
            setcountryList(list);
        }
        else{
            setcountryList(context.countryList);
        }
    }
    return (
        <>
            <Button className='countryDrop' onClick={()=>setisOpenModal(true)}>
                <div className='info d-flex flex-column'>
                   <span className='label'> Your Location</span>
                   <span className='name'>{context.selectedCountry!=="" ?
                    context.selectedCountry.substr(0,10)+'...' : 
                    'Select Location'}</span>
                </div>
                <span className='ml-auto'> <FaAngleDown/></span>
            </Button>

            <Dialog  open={isOpenModal} onClose={()=>setisOpenModal(false)}
             className='locationModel' TransitionComponent={Transition}>
                <h4 className='mb-0'> Choose Your Delivery Location</h4>
                <p> Enter your address and we will specify the offer for your area.</p>
                <Button className='close_' onClick={()=>setisOpenModal(false)}><MdClose/></Button>
                
                <div className='headerSearch w-100'>
                    <input type='text' placeholder='Search your area' onChange={filterList}/>
                    <Button> <IoIosSearch/> </Button>
                </div>

                <ul className='countryList mt-3'>
                    {
                        countryList?.length!==0 && countryList?.map((item, index)=>{
                            return(
                                <li key={index}> <Button onClick={()=>selectCountry(index, item.country)}
                                className={`${selectedTab===index ? 'active' : ''}`}
                                >{item.country}</Button></li>
                            )
                        })
                    }
                    
                    
                </ul>
            
            </Dialog>
        </>
    )
}
export default CountryDropdown;
