import Rating from '@mui/material/Rating';
import { TfiFullscreen } from "react-icons/tfi";
import Button from '@mui/material/Button';
import { FaRegHeart } from "react-icons/fa";
import ProductModal from '../ProductModal';
import { useState } from 'react';


const ProductItem = ()=>{

    const [isOpenProductModal, setisOpenProductModal] = useState(false);
    const viewProductDetails=(id)=>{
        setisOpenProductModal(true);

    }
    const closeProductModal=()=>{
        setisOpenProductModal(false);
    }
    return(
        <>
        <div className="item productItem">
            <div className="imgWrapper">
                <img src="https://m.media-amazon.com/images/I/61YamEA82eL._SY741_.jpg" className="w-100"/>

                <span className="badge badge-primary"> 28% </span>
                <div className="actions">
                    <Button onClick={()=>viewProductDetails(1)}><TfiFullscreen/></Button>
                    <Button><FaRegHeart style={{fontSize:'20px'}}/></Button>
                </div>
            </div>
            <div className="info">
                <h4>Cambric Floral Kurta for Women</h4>
                <span className="text-success d-block"> In Stock</span>
                <Rating className="mt-2 mb-2" name="read-only" value={5} readOnly size="small" precision={0.5}/>
                <div className="d-flex">
                    <span className="oldPrice">$20.00</span>
                    <span className="netPrice text-danger ml-2">$14.00</span>
                </div>
            </div>   
        </div>

            {
                isOpenProductModal ===true && <ProductModal closeProductModal={closeProductModal}/>
            }
        </>
    )

}
export default ProductItem;
